import React, { useState } from 'react';
import './styles/App.css';

function Login({ onLogin }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (username && password) {
            onLogin(username);
        } else {
            alert('Please enter username and password.');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="login-form">
            <h2>Login</h2>
            <div>
                <label>Username:</label>
                <input
                    type="text"
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                    required
                    style={{ width: "250px" }}
                />
            </div>
            <div>
                <label>Password:</label>
                <input
                    type="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    required
                    style={{ width: "250px" }}
                />
            </div>
            <button type="submit">Login</button>
        </form>
    );
}

function AddPayeePage({ onBack, onSubmit, newPayee, handleChange }) {
    return (
        <div className="add-payee-page" style={{ maxWidth: 400, margin: "40px auto", border: "1px solid #ccc", padding: 24, borderRadius: 8 }}>
            <h2>Add New Payee</h2>
            <form onSubmit={onSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={newPayee.name}
                        onChange={handleChange}
                        required
                        style={{ width: "250px", marginLeft: 10 }}
                    />
                </div>
                <div>
                    <label>Account Number:</label>
                    <input
                        type="text"
                        name="accountNumber"
                        value={newPayee.accountNumber}
                        onChange={handleChange}
                        required
                        style={{ width: "250px", marginLeft: 10 }}
                    />
                </div>
                <div>
                    <label>Sort Code:</label>
                    <input
                        type="text"
                        name="sortCode"
                        value={newPayee.sortCode}
                        onChange={handleChange}
                        required
                        style={{ width: "250px", marginLeft: 10 }}
                    />
                </div>
                <div style={{ marginTop: 20 }}>
                    <button type="submit" style={{ marginRight: 10 }}>Add Payee</button>
                    <button type="button" onClick={onBack}>Back</button>
                </div>
            </form>
        </div>
    );
}

function Dashboard({ username }) {
    const [showPayment, setShowPayment] = useState(false);
    const [selectedAccount, setSelectedAccount] = useState('');
    const [selectedToAccount, setSelectedToAccount] = useState('');
    const [showAddPayeePage, setShowAddPayeePage] = useState(false);
    const [newPayee, setNewPayee] = useState({
        accountNumber: '',
        sortCode: '',
        name: ''
    });

    // Example accounts, replace with real data as needed
    const userAccounts = [
        { id: 'acc1', name: 'Savings Account - 1234' },
        { id: 'acc2', name: 'Checking Account - 5678' }
    ];
    const existingAccounts = [
        { id: 'acc3', name: 'Friend Account - 4321' },
        { id: 'acc4', name: 'Vendor Account - 8765' }
    ];

    const handleNewPayeeChange = (e) => {
        const { name, value } = e.target;
        setNewPayee(prev => ({ ...prev, [name]: value }));
    };

    const handleAddPayee = (e) => {
        e.preventDefault();
        alert(`New payee added:\nName: ${newPayee.name}\nAccount Number: ${newPayee.accountNumber}\nSort Code: ${newPayee.sortCode}`);
        setShowAddPayeePage(false);
        setNewPayee({ accountNumber: '', sortCode: '', name: '' });
    };

    if (showAddPayeePage) {
        return (
            <AddPayeePage
                onBack={() => setShowAddPayeePage(false)}
                onSubmit={handleAddPayee}
                newPayee={newPayee}
                handleChange={handleNewPayeeChange}
            />
        );
    }

    return (
        <div className="App">
            <h1>Payment UI</h1>
            <h2>Welcome, {username}!</h2>
            {!showPayment && (
                <button onClick={() => setShowPayment(true)}>
                    Initiate Payment
                </button>
            )}
            {showPayment && (
                <>
                    <div style={{ margin: "20px 0" }}>
                        <label htmlFor="fromAccount"><b>From:</b></label>
                        <select
                            id="fromAccount"
                            value={selectedAccount}
                            onChange={e => setSelectedAccount(e.target.value)}
                            style={{ marginLeft: 10, width: "250px" }}
                        >
                            <option value="">Select your account</option>
                            {userAccounts.map(acc => (
                                <option key={acc.id} value={acc.id}>{acc.name}</option>
                            ))}
                        </select>
                    </div>
                    <div style={{ margin: "20px 0" }}>
                        <label htmlFor="toAccount"><b>To (Existing Account):</b></label>
                        <select
                            id="toAccount"
                            value={selectedToAccount}
                            onChange={e => setSelectedToAccount(e.target.value)}
                            style={{ marginLeft: 10, width: "250px" }}
                        >
                            <option value="">Select existing account</option>
                            {existingAccounts.map(acc => (
                                <option key={acc.id} value={acc.id}>{acc.name}</option>
                            ))}
                        </select>
                    </div>
                    <button onClick={() => setShowAddPayeePage(true)} style={{ marginBottom: 20 }}>
                        Add New Payee
                    </button>
                </>
            )}
        </div>
    );
}

function App() {
    const [loggedInUser, setLoggedInUser] = useState(null);

    return (
        <div>
            {!loggedInUser ? (
                <Login onLogin={setLoggedInUser} />
            ) : (
                <Dashboard username={loggedInUser} />
            )}
        </div>
    );
}

export default App;
