import React, { useState } from 'react';
import SenderForm from './components/SenderForm';
import ReceiverForm from './components/ReceiverForm';
import './styles/App.css';

function Login({ onLogin }) {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (username && password) {
            onLogin(username);
        } else {
            alert('Please enter username and password.');
        }
    };

    return (
        <form onSubmit={handleSubmit} className="login-form">
            <h2>Login</h2>
            <div>
                <label>Username:</label>
                <input
                    type="text"
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                    required
                    style={{ width: "250px" }}
                />
            </div>
            <div>
                <label>Password:</label>
                <input
                    type="password"
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                    required
                     style={{ width: "250px" }}
                />
            </div>
            <button type="submit">Login</button>
        </form>
    );
}

function Dashboard({ username }) {
    const [showPayment, setShowPayment] = useState(false);
    const [selectedAccount, setSelectedAccount] = useState('');
    // Example accounts, replace with real data as needed
    const userAccounts = [
        { id: 'acc1', name: 'Savings Account - 1234' },
        { id: 'acc2', name: 'Checking Account - 5678' }
    ];

    return (
        <div className="App">
            <h1>Payment UI</h1>
            <h2>Welcome, {username}!</h2>
            {!showPayment && (
                <button onClick={() => setShowPayment(true)}>
                    Initiate Payment
                </button>
            )}
            {showPayment && (
                <>
                    <div style={{ margin: "20px 0" }}>
                        <label htmlFor="fromAccount"><b>From:</b></label>
                        <select
                            id="fromAccount"
                            value={selectedAccount}
                            onChange={e => setSelectedAccount(e.target.value)}
                            style={{ marginLeft: 10, width: "250px" }}
                        >
                            <option value="">Select your account</option>
                            {userAccounts.map(acc => (
                                <option key={acc.id} value={acc.id}>{acc.name}</option>
                            ))}
                        </select>
                    </div>
                    <SenderForm />
                    <ReceiverForm />
                </>
            )}
        </div>
    );
}

function App() {
    const [loggedInUser, setLoggedInUser] = useState(null);

    return (
        <div>
            {!loggedInUser ? (
                <Login onLogin={setLoggedInUser} />
            ) : (
                <Dashboard username={loggedInUser} />
            )}
        </div>
    );
}

export default App;
